kickstart.post:
if [ '&bootdisk;' == 'sda' ]
then
	BOOTPART=sda1
	ROOTPART=sda2
fi

if [ '&bootdisk;' == 'mmcblk0' ]
then
	BOOTPART=mmcblk0p1
	ROOTPART=mmcblk0p2
fi




dwc_otg.lpm_enable=0 root=/dev/$ROOTPART rootfstype=ext4 elevator=deadline rootwait frontend=&Kickstart_PrivateKickstartHost;





rm -f /etc/yum.repos.d/CentOS*.repo




&hostname;
&hostname;
&hostname;




timedatectl set-timezone &Kickstart_Timezone;
timedatectl set-ntp true


[Time]
NTP=&Kickstart_PrivateNTPHost;
